    const newTodo = {
      id: Math.floor(Math.random() * 1e5),
      content: inputVal
    };

    createTodo(newTodo);
  }
  return (
  <div>
    <input 
      type="text" 
      value={enteredTodo} 
      onChange={e => setEnteredTodo(e.target.value)}/>
    <button onClick={addTodo}>追加</button>
